import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import { API_BASE_URL } from '../../App';
import { Modal, Button, Form, Badge, Table, Spinner, Alert, Row, Col } from 'react-bootstrap';
// Utils for formatting time - Currency formatting removed as cost is removed from task
import { formatDuration } from '../../utils/formatting'; // Adjust path if needed
import TimeLogForm from './TimeLogForm'; // Import TimeLogForm

// Helper functions
const getTaskStatusColor = (status) => {
  switch (status?.toLowerCase()) {
    case 'pending': return 'secondary';
    case 'in progress': return 'info';
    case 'completed': return 'success';
    default: return 'light';
  }
};
const getTaskPriorityColor = (priority) => {
  switch (priority) {
    case 'low': return 'success';
    case 'medium': return 'warning';
    case 'high': return 'danger';
    default: return 'light';
  }
};


function TaskTable() {
  const { token } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [modalError, setModalError] = useState(null);

  // State for Edit/Create Task Modal
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);
  // Removed actual_cost from initial state
  const [formData, setFormData] = useState({
    project_id: '', title: '', description: '', status: 'pending', priority: 'medium',
    assigned_user_id: '', due_date: '',
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  // State for Time Log Modal
  const [showTimeLogModal, setShowTimeLogModal] = useState(false);
  const [selectedTaskForLog, setSelectedTaskForLog] = useState(null);

  const taskStatuses = ['pending', 'in progress', 'completed'];
  const taskPriorities = ['low', 'medium', 'high'];

  // Data Fetching Logic (ensure backend sends total_time_spent)
  const fetchData = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const [tasksResponse, projectsResponse, usersResponse] = await Promise.all([
        fetch(`${API_BASE_URL}/tasks`, { headers: { 'Authorization': `Bearer ${token}`, 'Accept': 'application/json' } }),
        fetch(`${API_BASE_URL}/projects`, { headers: { 'Authorization': `Bearer ${token}`, 'Accept': 'application/json' } }),
        fetch(`${API_BASE_URL}/users`, { headers: { 'Authorization': `Bearer ${token}`, 'Accept': 'application/json' } }),
      ]);

      if (!tasksResponse.ok) throw new Error(`Task fetch failed: ${tasksResponse.status} ${tasksResponse.statusText}`);
      if (!projectsResponse.ok) throw new Error(`Project fetch failed: ${projectsResponse.status} ${projectsResponse.statusText}`);
      if (!usersResponse.ok) throw new Error(`User fetch failed: ${usersResponse.status} ${usersResponse.statusText}`);

      const tasksData = await tasksResponse.json();
      const projectsData = await projectsResponse.json();
      const usersData = await usersResponse.json();

      setTasks(Array.isArray(tasksData) ? tasksData : (tasksData?.data ?? []));
      setProjects(projectsData?.data ?? (Array.isArray(projectsData) ? projectsData : []));
      setUsers(usersData?.data ?? (Array.isArray(usersData) ? usersData : []));

    } catch (e) {
      console.error('Failed to fetch data:', e);
      setError(`Failed to load data: ${e.message}.`);
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // --- Edit/Create Modal Handlers ---
  const handleShowCreateModal = () => {
    if (projects.length === 0 && !loading) { /* ... error handling ... */ return; }
    if (projects.length > 0) {
        setIsEditing(false);
        setCurrentTask(null);
        setModalError(null);
        setIsSubmitting(false);
        // Reset form data, excluding status (hidden) and actual_cost (removed)
        setFormData({
            project_id: projects[0]?.id || '', title: '', description: '',
            status: 'pending', priority: 'medium', assigned_user_id: '',
            due_date: '',
            // actual_cost: '', // Removed
        });
        setShowModal(true);
    } else if (!loading) { /* ... error handling ... */ }
  };
  const handleShowEditModal = (task) => {
    setIsEditing(true);
    setCurrentTask(task);
    setModalError(null);
    setIsSubmitting(false);
    // Populate form data, excluding actual_cost (removed)
    setFormData({
      project_id: task.project_id, title: task.title, description: task.description || '',
      status: task.status || 'pending', priority: task.priority || 'medium',
      assigned_user_id: task.assigned_user_id || '',
      due_date: task.due_date ? task.due_date.split('T')[0] : '',
      // actual_cost: '', // Removed
    });
    setShowModal(true);
  };
  const handleCloseModal = () => {
      if (isSubmitting) return;
      setShowModal(false);
      setCurrentTask(null);
      setModalError(null);
  };
  const handleInputChange = (e) => {
      const { name, value } = e.target;
      setFormData(prev => ({ ...prev, [name]: value }));
  };

  // --- Form Submission ---
  const handleSubmit = async (e) => {
    e.preventDefault();
    setModalError(null);

    // Frontend Validation (removed cost validation)
    if (!formData.title.trim()) { setModalError('Task title cannot be empty.'); return; }
    if (!formData.project_id) { setModalError('Please select a project.'); return; }
    if (formData.due_date && isNaN(Date.parse(formData.due_date))) { setModalError('Invalid Due Date format.'); return; }

    setIsSubmitting(true);

    const url = isEditing ? `${API_BASE_URL}/tasks/${currentTask.id}` : `${API_BASE_URL}/tasks`;
    const method = isEditing ? 'PUT' : 'POST';

    // Prepare Payload (removed actual_cost)
    const payload = { ...formData };
    payload.assigned_user_id = formData.assigned_user_id === '' || formData.assigned_user_id === null ? null : parseInt(formData.assigned_user_id, 10);
    payload.project_id = parseInt(formData.project_id, 10);
    if (!formData.due_date) delete payload.due_date;
    payload.description = formData.description || '';
    // delete payload.actual_cost; // No longer exists in formData

    // Backend handles setting status='pending' if creating (method === 'POST')

    console.log('Payload sending:', JSON.stringify(payload, null, 2));

    try {
      const response = await fetch(url, {
        method,
        headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json', 'Accept': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (!response.ok) { /* ... error handling ... */ throw new Error(message); }
      fetchData();
      handleCloseModal();
    } catch (e) { /* ... error handling ... */ }
    finally { setIsSubmitting(false); }
  };

  // --- Deletion Handler ---
  const handleDelete = async (taskId) => { /* ... (remains same) ... */ };

  // --- Time Log Modal Handlers ---
  const handleShowTimeLogModal = (task) => { /* ... (remains same) ... */ };
  const handleCloseTimeLogModal = () => { /* ... (remains same) ... */ };
  const handleTimeLogSaveSuccess = () => { /* ... (remains same) ... */ };


  // --- Render Logic ---
  if (loading) return ( <div className="d-flex justify-content-center align-items-center text-light vh-100"><Spinner animation="border" className="me-2"/> Loading...</div> );
  if (error && !showModal && !showTimeLogModal) return ( <Alert variant="danger" className="bg-dark text-light border-danger">{error}</Alert> );

  return (
    <div className="p-md-4" style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #232323, #1a1a1a)' }}>
      <div className="bg-dark p-4 rounded shadow mb-4">
        {/* Header, Create Button, Info Messages */}
        <div className="d-flex justify-content-between align-items-center mb-3 flex-wrap"> {/* ... */} </div>
        {/* ... Alerts ... */}

        {/* Task Table */}
        {tasks.length > 0 && (
          <div className="table-responsive">
            <Table variant="dark" striped hover responsive="sm" className="align-middle">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Project</th>
                  <th>Status</th>
                  <th>Priority</th>
                  <th>Due Date</th>
                  <th>Assignee</th>
                  {/* REMOVED Cost Header <th className="text-end">Cost</th> */}
                  <th className="text-end">Time Spent</th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map(task => (
                  <tr key={task.id}>
                    <td className="fw-bold">{task.title}</td>
                    <td>{task.project?.name || <span className="text-muted">N/A</span>}</td>
                    <td><Badge bg={getTaskStatusColor(task.status)} className="text-capitalize p-2">{task.status || 'N/A'}</Badge></td>
                    <td><Badge bg={getTaskPriorityColor(task.priority)} className="text-capitalize p-2">{task.priority || 'N/A'}</Badge></td>
                    <td>{task.due_date ? new Date(task.due_date).toLocaleDateString() : <span className="text-muted">N/A</span>}</td>
                    <td>{task.assigned_user?.name || <span className="text-muted">Unassigned</span>}</td>
                    {/* REMOVED Cost Cell */}
                    <td className="text-end">{task.total_time_spent !== null && task.total_time_spent > 0 ? formatDuration(task.total_time_spent) : <span className="text-muted">0m</span>}</td>
                    <td className="text-center">
                       <Button variant="outline-info" size="sm" onClick={() => handleShowTimeLogModal(task)} className="me-2 py-1 px-2" title="Log Time"><i className="bi bi-clock-history me-1"></i> Log</Button>
                       <Button variant="outline-light" size="sm" onClick={() => handleShowEditModal(task)} className="me-2 py-1 px-2" title="Edit Task"><i className="bi bi-pencil-fill me-1"></i> Edit</Button>
                       <Button variant="outline-danger" size="sm" onClick={() => handleDelete(task.id)} title="Delete Task" className="py-1 px-2"><i className="bi bi-trash-fill me-1"></i> Delete</Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        )}
      </div>

      {/* --- Task Edit/Create Modal --- */}
      <Modal show={showModal} onHide={handleCloseModal} backdrop="static" keyboard={false} centered>
        <Modal.Header closeButton={!isSubmitting} closeVariant="white" className="bg-dark border-secondary">
          <Modal.Title className="text-light">{isEditing ? 'Edit Task' : 'Create New Task'}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body className="bg-dark text-light">
             {modalError && <Alert variant="danger" className="bg-dark text-light border-danger" onClose={() => setModalError(null)} dismissible>{modalError}</Alert>}

             {/* Project Select */}
             <Form.Group className="mb-3" controlId="taskProjectId">{/* ... */}</Form.Group>
             {/* Title Input */}
             <Form.Group className="mb-3" controlId="taskTitle">{/* ... */}</Form.Group>
             {/* Description Textarea */}
             <Form.Group className="mb-3" controlId="taskDescription">{/* ... */}</Form.Group>

             {/* --- Status (Conditional) & Priority --- */}
             <Row className="mb-3">
                {isEditing && ( // Only show Status when editing
                    <Col md={6}>
                         <Form.Group controlId="taskStatus">
                           <Form.Label>Status</Form.Label>
                           <Form.Select name="status" value={formData.status} onChange={handleInputChange} className="bg-secondary text-white border-secondary" disabled={isSubmitting}>
                             {taskStatuses.map(s => (<option key={s} value={s} className="text-capitalize">{s}</option>))}
                           </Form.Select>
                         </Form.Group>
                    </Col>
                )}
                 <Col md={isEditing ? 6 : 12}> {/* Adjust Priority width */}
                     <Form.Group controlId="taskPriority">
                       <Form.Label>Priority</Form.Label>
                       <Form.Select name="priority" value={formData.priority} onChange={handleInputChange} className="bg-secondary text-white border-secondary" disabled={isSubmitting}>
                         {taskPriorities.map(p => (<option key={p} value={p} className="text-capitalize">{p}</option>))}
                       </Form.Select>
                     </Form.Group>
                 </Col>
             </Row>
             {/* --- End Status & Priority --- */}

             {/* Due Date & Assignee Row */}
             <Row className="mb-3"> <Col md={6}><Form.Group controlId="taskDueDate">{/* ... */}</Form.Group></Col> <Col md={6}><Form.Group controlId="taskAssignee">{/* ... */}</Form.Group></Col> </Row>

             {/* --- REMOVED Actual Cost Row --- */}

          </Modal.Body>
          <Modal.Footer className="bg-dark border-secondary">
            <Button variant="secondary" onClick={handleCloseModal} disabled={isSubmitting}>Cancel</Button>
            <Button variant="primary" type="submit" disabled={isSubmitting}>
               {isSubmitting ? (<>...</>) : (isEditing ? 'Save Changes' : 'Create Task')}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
      {/* --- End Task Edit/Create Modal --- */}


      {/* --- Time Log Modal Rendering --- */}
      {selectedTaskForLog && ( <TimeLogForm /* ... props ... */ /> )}
      {/* --- End Time Log Modal Rendering --- */}

    </div>
  );
}

export default TaskTable;
