import React from 'react';
import { Link, Outlet, useNavigate, NavLink } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import NotificationBell from '../Notifications/NotificationBell'; // <-- IMPORTED

function Dashboard() {
  const { logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login'); // Redirect to login after logout
  };

  return (
    // Main container with dark gradient background
    <div className="d-flex flex-column min-vh-100" style={{ background: 'linear-gradient(135deg, #1f1f1f, #121212)' }}>

      {/* Navbar */}
      <Navbar expand="lg" variant="dark" bg="dark" className="shadow-sm">
        <Container fluid>
          {/* Brand Logo/Name */}
          <Navbar.Brand as={Link} to="/dashboard" className="fw-bold text-white">
            <i className="bi bi-kanban-fill me-2"></i>Klick Inc. {/* Example Icon */}
          </Navbar.Brand>

          {/* Navbar Toggle for smaller screens */}
          <Navbar.Toggle aria-controls="dashboard-navbar-nav" />

          {/* Navbar Content */}
          <Navbar.Collapse id="dashboard-navbar-nav">
            {/* Left-aligned Navigation Links */}
            <Nav className="me-auto">
              <Nav.Link
                as={NavLink}
                to="/dashboard/projects"
                // Style active link
                className={({ isActive }) => isActive ? 'text-white fw-bold' : 'text-secondary'}
              >
                Projects
              </Nav.Link>
              <Nav.Link
                as={NavLink}
                to="/dashboard/tasks"
                // Style active link
                className={({ isActive }) => isActive ? 'text-white fw-bold' : 'text-secondary'}
              >
                Tasks
              </Nav.Link>
              {/* Add other main navigation links here if needed */}
            </Nav>

            {/* Right-aligned Items (Notifications & Logout) */}
            <Nav className="ms-auto d-flex align-items-center">
              {/* --- ADDED Notification Bell --- */}
              <NotificationBell />
              {/* --- END Notification Bell --- */}

              {/* Logout Button */}
              <Button variant="outline-light" onClick={handleLogout} className="ms-2"> {/* Added margin-start */}
                <i className="bi bi-box-arrow-right me-1"></i>Logout
              </Button>
            </Nav>
            {/* --- End Right-aligned Section --- */}

          </Navbar.Collapse>
        </Container>
      </Navbar>
      {/* End Navbar */}


      {/* Main Content Area */}
      <Container fluid as="main" className="flex-grow-1 my-4">
        {/* Inner container for content styling */}
        <Container className="bg-dark text-white p-4 rounded shadow">
          {/* Child routes will render here */}
          <Outlet />
        </Container>
      </Container>
      {/* End Main Content */}


      {/* Footer */}
      <footer className="text-center text-secondary py-3 mt-auto">
        Â© {new Date().getFullYear()} Klick Inc. All rights reserved.
      </footer>
      {/* End Footer */}

    </div>
  );
}

export default Dashboard;
