import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import { API_BASE_URL } from '../../App';
import { Modal, Button, Form, Badge, Table, Spinner, Alert, Row, Col } from 'react-bootstrap';
// Utils for formatting time and potentially currency (if needed elsewhere)
import { formatDuration, formatDate } from '../../utils/formatting'; // Removed formatCurrency if not used here

// Helper functions
const getTaskStatusColor = (status) => {
  switch (status?.toLowerCase()) {
    case 'pending': return 'secondary';
    case 'in progress': return 'info';
    case 'completed': return 'success';
    default: return 'light';
  }
};
const getTaskPriorityColor = (priority) => {
  switch (priority) {
    case 'low': return 'success';
    case 'medium': return 'warning';
    case 'high': return 'danger';
    default: return 'light';
  }
};


function TaskTable() {
  const { token } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // For general fetch/delete/timer errors
  const [modalError, setModalError] = useState(null); // Separate state for modal errors

  // State for Edit/Create Task Modal
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false); // Controls modal mode and conditional fields
  const [currentTask, setCurrentTask] = useState(null);
  // formData no longer includes actual_cost
  const [formData, setFormData] = useState({
    project_id: '', title: '', description: '', status: 'pending', priority: 'medium',
    assigned_user_id: '', due_date: '',
  });
  const [isSubmitting, setIsSubmitting] = useState(false); // For modal submit button

  // State for Start/Stop Button Loading
  const [timerLoadingTaskId, setTimerLoadingTaskId] = useState(null);

  // REMOVED Time Log Modal State

  const taskStatuses = ['pending', 'in progress', 'completed'];
  const taskPriorities = ['low', 'medium', 'high'];

  // Data Fetching Logic (ensure backend sends started_at, ended_at, and calculated duration)
  const fetchData = useCallback(async () => {
    setLoading(true); setError(null);
    try {
        const [tRes, pRes, uRes] = await Promise.all([
            fetch(`${API_BASE_URL}/tasks`, { headers: { 'Authorization': `Bearer ${token}`, 'Accept': 'application/json' } }),
            fetch(`${API_BASE_URL}/projects`, { headers: { 'Authorization': `Bearer ${token}`, 'Accept': 'application/json' } }),
            fetch(`${API_BASE_URL}/users`, { headers: { 'Authorization': `Bearer ${token}`, 'Accept': 'application/json' } }),
        ]);

        if (!tRes.ok) throw new Error(`Task fetch failed: ${tRes.status} ${tRes.statusText}`);
        if (!pRes.ok) throw new Error(`Project fetch failed: ${pRes.status} ${pRes.statusText}`);
        if (!uRes.ok) throw new Error(`User fetch failed: ${uRes.status} ${uRes.statusText}`);

        const tasksData = await tRes.json();
        const projectsData = await pRes.json();
        const usersData = await uRes.json();

        console.log("DEBUG: First Task Received:", tasksData[0] || tasksData?.data?.[0]); // Check data structure
        setTasks(Array.isArray(tasksData) ? tasksData : (tasksData?.data ?? []));
        setProjects(projectsData?.data ?? (Array.isArray(projectsData) ? projectsData : []));
        setUsers(usersData?.data ?? (Array.isArray(usersData) ? usersData : []));

    } catch (e) {
        console.error('Failed to fetch data:', e);
        setError(`Failed to load data: ${e.message}.`);
        setTasks([]); setProjects([]); setUsers([]); // Clear data on error
    } finally {
        setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchData();
  }, [fetchData]); // Dependency on fetchData is correct due to useCallback

  // --- Edit/Create Modal Handlers ---
  const handleShowCreateModal = () => {
      if (projects.length === 0 && !loading) { setError('Cannot add tasks: No projects available.'); return; }
      if (projects.length > 0) {
          setIsEditing(false); setCurrentTask(null); setModalError(null); setIsSubmitting(false);
          setFormData({ project_id: projects[0]?.id || '', title: '', description: '', status: 'pending', priority: 'medium', assigned_user_id: '', due_date: '', });
          setShowModal(true);
      } else if (!loading) { setError('No projects found to assign tasks to.'); }
  };
  const handleShowEditModal = (task) => {
      setIsEditing(true); setCurrentTask(task); setModalError(null); setIsSubmitting(false);
      setFormData({ project_id: task.project_id, title: task.title, description: task.description || '', status: task.status || 'pending', priority: task.priority || 'medium', assigned_user_id: task.assigned_user_id || '', due_date: task.due_date ? task.due_date.split('T')[0] : '', });
      setShowModal(true);
  };
  const handleCloseModal = () => {
      if (isSubmitting) return;
      setShowModal(false); setCurrentTask(null); setModalError(null);
  };
  const handleInputChange = (e) => {
      const { name, value } = e.target;
      setFormData(prev => ({ ...prev, [name]: value }));
  };

  // --- Form Submission ---
  const handleSubmit = async (e) => {
    e.preventDefault(); setModalError(null);

    // Validation
    if (!formData.title.trim()) { setModalError('Task title cannot be empty.'); return; }
    if (!formData.project_id) { setModalError('Please select a project.'); return; }
    if (formData.due_date && isNaN(Date.parse(formData.due_date))) { setModalError('Invalid Due Date format.'); return; }

    setIsSubmitting(true);
    const url = isEditing ? `${API_BASE_URL}/tasks/${currentTask.id}` : `${API_BASE_URL}/tasks`;
    const method = isEditing ? 'PUT' : 'POST';

    // Payload
    const payload = { ...formData };
    payload.assigned_user_id = formData.assigned_user_id === '' || formData.assigned_user_id === null ? null : parseInt(formData.assigned_user_id, 10);
    payload.project_id = parseInt(formData.project_id, 10);
    if (!formData.due_date) delete payload.due_date;
    payload.description = formData.description || '';

    console.log('Payload sending:', JSON.stringify(payload, null, 2));

    try {
      const response = await fetch(url, { method, headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json', 'Accept': 'application/json' }, body: JSON.stringify(payload) });
      if (!response.ok) {
         let message = `HTTP error: ${response.status}`; // Define message here
         try {
             const errorData = await response.json();
             // Attempt to get more specific message from backend
             message = errorData.errors ? Object.values(errorData.errors).flat().join(' ') : (errorData.message || message);
         } catch(jsonError) { console.error("Non-JSON error response: ", await response.text()); } // Log if not JSON
         throw new Error(message); // Throw the detailed error
      }
      fetchData(); handleCloseModal();
    } catch (e) { // Catch the error object 'e'
      console.error('Failed to save task:', e);
      setModalError(`Failed to save task: ${e.message}`); // *** CORRECTED: Use e.message ***
    } finally {
       setIsSubmitting(false);
    }
  };

  // --- Deletion Handler ---
  const handleDelete = async (taskId) => {
      if (!window.confirm('Are you sure you want to permanently delete this task?')) return;
      setError(null);
      try {
          const response = await fetch(`${API_BASE_URL}/tasks/${taskId}`, { method: 'DELETE', headers: { 'Authorization': `Bearer ${token}`, 'Accept': 'application/json' } });
          if (!response.ok && response.status !== 204) {
             let message = `HTTP error: ${response.status}`; // Define message here
             try { const errorData = await response.json(); message = errorData.message || message; }
             catch(jsonError) { /* Use default message */ }
             throw new Error(message); // Throw the detailed error
          }
          fetchData();
      } catch (e) { // Catch the error object 'e'
          console.error('Failed to delete task:', e);
          setError(`Failed to delete task: ${e.message}`); // *** CORRECTED: Use e.message ***
      }
  };

  // --- Start/Stop Timer Handler ---
  const handleTimerAction = async (taskId, action) => {
      setTimerLoadingTaskId(taskId); setError(null);
      const url = `${API_BASE_URL}/tasks/${taskId}/${action}-timer`;
      try {
          const response = await fetch(url, { method: 'POST', headers: { Authorization: `Bearer ${token}`, Accept: 'application/json', 'Content-Type': 'application/json' } });
          if (!response.ok) {
             let message = `HTTP error ${response.status}`; // Define message here
             try{ const err = await response.json(); message = err.message || message; } catch(jsonError){}
             throw new Error(message); // Throw the detailed error
          }
          fetchData();
      } catch (e) { // Catch the error object 'e'
          console.error(`Failed to ${action} timer:`, e);
          setError(`Failed to ${action} timer: ${e.message}`); // *** CORRECTED: Use e.message ***
      } finally {
          setTimerLoadingTaskId(null);
      }
  };


  // --- Render Logic ---
  if (loading) return ( <div className="d-flex justify-content-center align-items-center text-light vh-100"><Spinner animation="border" className="me-2"/> Loading...</div> );
  if (error && !showModal) return ( <Alert variant="danger" className="bg-dark text-light border-danger">{error}</Alert> );

  return (
    <div className="p-md-4" style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #232323, #1a1a1a)' }}>
      <div className="bg-dark p-4 rounded shadow mb-4">
        {/* Header and Create Button */}
        <div className="d-flex justify-content-between align-items-center mb-3 flex-wrap">
          <h2 className="text-light mb-2 mb-md-0">Tasks</h2>
          <Button variant="primary" onClick={handleShowCreateModal} disabled={projects.length === 0 && !loading}>
            <i className="bi bi-plus-lg me-1"></i> Create New Task
          </Button>
        </div>

         {/* Display Errors/Info Messages */}
         {error && !showModal && <Alert variant="danger" className="bg-dark text-light border-danger">{error}</Alert>}
         {projects.length === 0 && !loading && <Alert variant="warning" className="bg-dark text-warning border-warning">Create a project before adding tasks.</Alert>}
         {tasks.length === 0 && projects.length > 0 && !loading && <Alert variant="info" className="bg-dark text-info border-info mt-3">No tasks found. Click 'Create New Task' to add one.</Alert>}

        {/* Task Table */}
        {tasks.length > 0 && (
          <div className="table-responsive">
            <Table variant="dark" striped hover responsive="sm" className="align-middle">
              <thead>
                <tr>
                  <th>Title</th><th>Project</th><th>Status</th><th>Priority</th><th>Due Date</th><th>Assignee</th>
                  <th className="text-end">Started</th>
                  <th className="text-end">Ended</th>
                  <th className="text-end">Duration</th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map(task => {
                  const isTimerRunning = task.started_at && !task.ended_at;
                  const canStart = !task.started_at;
                  const canStop = isTimerRunning;
                  const isLoading = timerLoadingTaskId === task.id;

                  return (
                      <tr key={task.id}>
                        <td className="fw-bold">{task.title}</td>
                        <td>{task.project?.name || <span className="text-muted">N/A</span>}</td>
                        <td><Badge bg={getTaskStatusColor(task.status)} className="text-capitalize p-2">{task.status || 'N/A'}</Badge></td>
                        <td><Badge bg={getTaskPriorityColor(task.priority)} className="text-capitalize p-2">{task.priority || 'N/A'}</Badge></td>
                        <td>{task.due_date ? new Date(task.due_date).toLocaleDateString() : <span className="text-muted">N/A</span>}</td>
                        <td>{task.assigned_user?.name || <span className="text-muted">Unassigned</span>}</td>
                        <td className="text-end">{formatDate(task.started_at)}</td>
                        <td className="text-end">{formatDate(task.ended_at)}</td>
                        <td className="text-end">{formatDuration(task.duration)}</td>
                        <td className="text-center">
                           {/* Conditional Start/Stop Button */}
                           {!isTimerRunning && canStart && (
                               <Button variant="outline-success" size="sm" onClick={() => handleTimerAction(task.id, 'start')} className="me-2 py-1 px-2" title="Start Timer" disabled={isLoading}>
                                   {isLoading ? <Spinner as="span" animation="border" size="sm" /> : <><i className="bi bi-play-fill me-1"></i> Start</>}
                               </Button>
                           )}
                           {isTimerRunning && canStop && (
                               <Button variant="outline-warning" size="sm" onClick={() => handleTimerAction(task.id, 'stop')} className="me-2 py-1 px-2" title="Stop Timer" disabled={isLoading}>
                                   {isLoading ? <Spinner as="span" animation="border" size="sm" /> : <><i className="bi bi-stop-fill me-1"></i> Stop</>}
                               </Button>
                           )}
                           {/* Edit Button */}
                           <Button variant="outline-light" size="sm" onClick={() => handleShowEditModal(task)} className="me-2 py-1 px-2" title="Edit Task"><i className="bi bi-pencil-fill me-1"></i> Edit</Button>
                           {/* Delete Button */}
                           <Button variant="outline-danger" size="sm" onClick={() => handleDelete(task.id)} title="Delete Task" className="py-1 px-2"><i className="bi bi-trash-fill me-1"></i> Delete</Button>
                        </td>
                      </tr>
                  );
                })}
              </tbody>
            </Table>
          </div>
        )}
      </div>

      {/* --- Task Edit/Create Modal --- */}
      <Modal show={showModal} onHide={handleCloseModal} backdrop="static" keyboard={false} centered>
        <Modal.Header closeButton={!isSubmitting} closeVariant="white" className="bg-dark border-secondary">
          <Modal.Title className="text-light">{isEditing ? 'Edit Task' : 'Create New Task'}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body className="bg-dark text-light">
             {modalError && <Alert variant="danger" className="bg-dark text-light border-danger" onClose={() => setModalError(null)} dismissible>{modalError}</Alert>}

             {/* Form Groups */}
             <Form.Group className="mb-3" controlId="taskProjectId">{/* ... */}</Form.Group>
             <Form.Group className="mb-3" controlId="taskTitle">{/* ... */}</Form.Group>
             <Form.Group className="mb-3" controlId="taskDescription">{/* ... */}</Form.Group>
             <Row className="mb-3">
                {isEditing && ( <Col md={6}><Form.Group controlId="taskStatus">{/* ... */}</Form.Group></Col> )}
                <Col md={isEditing ? 6 : 12}><Form.Group controlId="taskPriority">{/* ... */}</Form.Group></Col>
             </Row>
             <Row className="mb-3">
                <Col md={6}><Form.Group controlId="taskDueDate">{/* ... */}</Form.Group></Col>
                <Col md={6}><Form.Group controlId="taskAssignee">{/* ... */}</Form.Group></Col>
             </Row>
             {/* No Actual Cost Field */}
          </Modal.Body>
          <Modal.Footer className="bg-dark border-secondary">
            <Button variant="secondary" onClick={handleCloseModal} disabled={isSubmitting}>Cancel</Button>
            <Button variant="primary" type="submit" disabled={isSubmitting}>
               {isSubmitting ? (<>...</>) : (isEditing ? 'Save Changes' : 'Create Task')}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
      {/* --- End Task Edit/Create Modal --- */}

      {/* --- Time Log Modal REMOVED --- */}

    </div>
  );
}

export default TaskTable;
