import React, { useState, useEffect, useCallback } from 'react';
import { Dropdown, Badge, Spinner, ListGroup, Button, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext'; // Adjust path if needed
import { API_BASE_URL } from '../../App'; // Adjust path if needed
import { formatDate } from '../../utils/formatting'; // Adjust path if needed

function NotificationBell() {
  const { token } = useAuth();
  const navigate = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isOpen, setIsOpen] = useState(false);

  // Fetch notifications from the API
  const fetchNotifications = useCallback(async () => {
    // --- FIX 1: Prevent fetch if already loading or no token ---
    if (isLoading || !token) {
       console.log('Fetch skipped:', { isLoading, hasToken: !!token }); // Debug log
       return;
    }

    console.log('Starting fetch...'); // Debug log
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/notifications`, {
        headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' },
      });

      if (!response.ok) {
        let errorMsg = `HTTP error ${response.status}`;
         try {
            const errData = await response.json();
            errorMsg = errData.message || errorMsg;
         } catch (parseError) { console.warn("Could not parse error response as JSON"); }
        throw new Error(errorMsg);
      }

      const data = await response.json();
      console.log("Notifications Data Received:", data); // DEBUG

      const fetchedNotifications = data.data || [];
      setNotifications(fetchedNotifications);
      const currentUnreadCount = fetchedNotifications.filter(n => !n.read_at).length;
      setUnreadCount(currentUnreadCount);

    } catch (err) {
      console.error("Failed to fetch notifications:", err);
      setError("Couldn't load notifications.");
      setUnreadCount(0);
      setNotifications([]);
    } finally {
      console.log('Finished fetch, setting loading to false.'); // Debug log
      setIsLoading(false); // <-- This should now be reached
    }
    // --- FIX 2: Remove isLoading from dependency array ---
  }, [token]); // Only depends on token now

  // Initial fetch on component mount
  useEffect(() => {
    // Check for token on initial mount as well
    if (token) {
        fetchNotifications();
    } else {
        console.log("Skipping initial fetch: No token");
    }
  }, [fetchNotifications, token]); // Add token here to trigger fetch on login

  // --- Rest of the component remains the same ---
  // handleToggle, handleMarkAsRead, handleNotificationClick, handleMarkAllAsRead, CustomToggle, render...

// (Keep the rest of your handleToggle, handleMarkAsRead, handleNotificationClick, handleMarkAllAsRead, CustomToggle, and return JSX logic the same)

// Make sure to include the rest of the component code here...
// Including the handle* functions and the return() JSX

  const handleToggle = (nextShowState) => {
     setIsOpen(nextShowState);
      // Optionally fetch only when opened if needed
     // if (nextShowState) {
     //    fetchNotifications();
     // }
  };

  const handleMarkAsRead = async (notificationId, e) => {
      e.stopPropagation();
      const targetNotification = notifications.find(n => n.id === notificationId);
      if (!targetNotification || targetNotification.read_at) return;

      const previousNotifications = [...notifications]; // Store previous state
      const previousUnreadCount = unreadCount;

      setNotifications(prev => prev.map(n => n.id === notificationId ? { ...n, read_at: new Date().toISOString() } : n));
      setUnreadCount(prev => Math.max(0, prev - 1));

      try {
          const response = await fetch(`${API_BASE_URL}/notifications/${notificationId}/read`, {
              method: 'PATCH',
              headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' },
          });
          if (!response.ok) { throw new Error(`Server failed to mark as read: ${response.status}`); }
      } catch (err) {
          console.error("Failed to mark notification as read:", err);
          setError("Failed to update notification status.");
          // Revert on error
          setNotifications(previousNotifications);
          setUnreadCount(previousUnreadCount);
      }
  };

    const handleNotificationClick = (notification, e) => {
        if (!notification.read_at) {
            handleMarkAsRead(notification.id, e);
        }
        if (notification.data?.action_url) {
            navigate(notification.data.action_url);
            setIsOpen(false);
        }
    };

    const handleMarkAllAsRead = async (e) => {
        e.stopPropagation();
        if (unreadCount === 0) return;

        const previousNotifications = [...notifications];
        const previousUnreadCount = unreadCount;

        setNotifications(prev => prev.map(n => !n.read_at ? { ...n, read_at: new Date().toISOString() } : n));
        setUnreadCount(0);

        try {
            const response = await fetch(`${API_BASE_URL}/notifications/mark-all-read`, {
                method: 'POST',
                headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' },
            });
            if (!response.ok) { throw new Error(`Server failed to mark all as read: ${response.status}`); }
        } catch (err) {
            console.error("Failed to mark all notifications as read:", err);
            setError("Failed to update all notifications.");
            setNotifications(previousNotifications);
            setUnreadCount(previousUnreadCount);
        }
    };

    const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
        <Button ref={ref} onClick={(e) => { e.preventDefault(); onClick(e); }} variant="link" className="position-relative text-decoration-none border-0 p-0 me-2" aria-label="Notifications" title={`${unreadCount} unread notifications`}>
            <i className="bi bi-bell-fill text-white fs-5"></i>
            {unreadCount > 0 && (<Badge pill bg="danger" className="position-absolute top-0 start-100 translate-middle" style={{ fontSize: '0.6em', padding: '0.3em 0.5em' }}>{unreadCount > 9 ? '9+' : unreadCount}<span className="visually-hidden">unread notifications</span></Badge>)}
        </Button>
    ));

    return (
        <Dropdown onToggle={handleToggle} show={isOpen} align="end">
            <Dropdown.Toggle as={CustomToggle} id="dropdown-notification-bell" />
            <Dropdown.Menu variant="dark" className="shadow mt-2 dropdown-menu-scrollable" style={{minWidth: '350px', maxWidth:'400px', maxHeight: '450px', overflowY: 'auto'}}>
                <Dropdown.Header className="d-flex justify-content-between align-items-center">
                    <span>Notifications</span>
                    {unreadCount > 0 && (<Button variant="outline-light" size="sm" onClick={handleMarkAllAsRead} title="Mark all as read">Mark All Read</Button>)}
                </Dropdown.Header>
                <Dropdown.Divider className="mt-1 mb-0"/>
                {isLoading && <div className="text-center p-3 text-muted"><Spinner animation="border" size="sm" /> Loading...</div>}
                {error && !isLoading && <Dropdown.ItemText><Alert variant="danger" className="mb-0 mx-2">{error}</Alert></Dropdown.ItemText>}
                {!isLoading && !error && notifications.length === 0 && (<Dropdown.ItemText className="text-muted text-center p-3">You have no notifications.</Dropdown.ItemText>)}
                {!isLoading && !error && notifications.length > 0 && (
                    <ListGroup variant="flush">
                        {notifications.map(notification => (
                            <ListGroup.Item key={notification.id} action onClick={(e) => handleNotificationClick(notification, e)} data-read={!!notification.read_at} className={`bg-dark text-light border-secondary px-3 py-2 d-flex flex-column ${!notification.read_at ? 'notification-unread fw-bold' : 'notification-read text-muted'}`} style={{ cursor: 'pointer', opacity: !notification.read_at ? 1 : 0.8 }}>
                                <div className="d-flex justify-content-between align-items-start mb-1">
                                    <small style={{fontSize: '0.9em', marginRight: '10px', flexGrow: 1}}>{notification.data?.message || 'Notification message missing.'}</small>
                                    {!notification.read_at && (<Button variant="outline-secondary" size="sm" className="ms-auto border-0 flex-shrink-0 p-0" style={{ lineHeight: '1' }} onClick={(e) => handleMarkAsRead(notification.id, e)} title="Mark as read" aria-label="Mark notification as read"><i className="bi bi-check-circle"></i></Button>)}
                                </div>
                                <small className="d-block text-info" style={{fontSize: '0.75em'}}>{formatDate(notification.created_at, { dateStyle: 'short', timeStyle: 'short' })}</small>
                            </ListGroup.Item>
                        ))}
                    </ListGroup>
                )}
            </Dropdown.Menu>
        </Dropdown>
    );
}

export default NotificationBell;
