import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import { API_BASE_URL } from '../../App';
import { Modal, Button, Form, Table, Badge, Spinner, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { formatCurrency } from '../../utils/formatting'; // Keep this
import { Row, Col } from 'react-bootstrap';

// Helper function for status colors
const getStatusColor = (status) => {
  switch (status) {
    case 'Not Started': return 'secondary';
    case 'In Progress': return 'primary';
    case 'On Hold': return 'warning';
    case 'Completed': return 'success';
    default: return 'light';
  }
};


function ProjectsTable() {
  const { token } = useAuth();
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [modalError, setModalError] = useState(null);

  // State for modal, editing, form data, submitting
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false); // Controls conditional rendering
  const [currentProject, setCurrentProject] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formData, setFormData] = useState({
    name: '', description: '', start_date: '', end_date: '', status: 'Not Started', budget: '', currency: 'USD',
  });

  const projectStatuses = ['Not Started', 'In Progress', 'On Hold', 'Completed'];
  const currencies = ['USD', 'EUR', 'GBP', 'JPY', 'CAD'];

  // Fetching logic (Backend now sends total_expenditure and updated remaining_budget)
  const fetchProjects = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/projects`, {
        method: 'GET',
        headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' },
      });
      if (!response.ok) throw new Error(`HTTP error: ${response.status} ${response.statusText}`);
      const data = await response.json();
      setProjects(data.data ? data.data : (Array.isArray(data) ? data : [])); // Handle pagination or direct array
    } catch (e) {
      console.error("Fetch projects error:", e);
      setError('Failed to load projects. Please check the network connection or try again later.');
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchProjects();
  }, [fetchProjects]);


  // Modal/Delete Handlers
  const handleShowCreateModal = () => {
        setIsEditing(false); // Set editing to false
        setCurrentProject(null);
        setModalError(null);
        setIsSubmitting(false);
        setFormData({
            name: '', description: '', start_date: '', end_date: '',
            status: 'Not Started', // Default state, field hidden
            budget: '', currency: 'USD',
        });
        setShowModal(true);
  };
  const handleShowEditModal = (project) => {
        setIsEditing(true); // Set editing to true
        setCurrentProject(project);
        setModalError(null);
        setIsSubmitting(false);
        setFormData({
            name: project.name,
            description: project.description || '',
            start_date: project.start_date ? project.start_date.split('T')[0] : '',
            end_date: project.end_date ? project.end_date.split('T')[0] : '',
            status: project.status || 'Not Started', // Populate for edit
            budget: project.budget !== null && project.budget !== undefined ? String(project.budget) : '',
            currency: project.currency || 'USD',
        });
        setShowModal(true);
  };
  const handleCloseModal = () => { /* ... (remains same) ... */ };
  const handleInputChange = (e) => { /* ... (remains same) ... */ };
  const handleSubmit = async (e) => { /* ... (remains same, backend handles status on create) ... */ };
  const handleDelete = async (id) => { /* ... (remains same) ... */ };

  // --- Render Logic ---
  if (loading) return ( <div className="d-flex justify-content-center align-items-center text-light vh-100"><Spinner animation="border" className="me-2"/> Loading Projects...</div> );
  if (error && !showModal) return ( <Alert variant="danger" className="bg-dark text-light border-danger">{error}</Alert> );


  return (
    <>
      {/* Header and Create Button */}
      <div className="d-flex justify-content-between align-items-center mb-3 flex-wrap"> {/* ... */} </div>

      {/* Display Errors/Info Messages */}
      {error && !showModal && <Alert variant="danger" className="bg-dark text-light border-danger">{error}</Alert>}
      {projects.length === 0 && !loading && <Alert variant="info" className="bg-dark text-info border-info">No projects found. Click 'Create New Project' to get started!</Alert>}

      {/* --- Projects Table - UPDATED --- */}
      {projects.length > 0 && (
        <div className="table-responsive">
          <Table variant="dark" striped hover responsive="sm" className="align-middle">
            <thead>
              <tr>
                <th>Name</th>
                <th>Status</th>
                <th className="text-end">Budget</th>
                {/* --- REVERTED Header --- */}
                <th className="text-end">Spent</th>
                <th className="text-end">Remaining Budget</th>
                <th className="text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {projects.map(proj => (
                <tr key={proj.id}>
                  {/* Name, Status, Budget Cells */}
                  <td><Link to={`/dashboard/projects/${proj.id}`} className="text-decoration-none text-info hover-underline fw-bold">{proj.name}</Link>{proj.description && <div className="small text-muted">{proj.description.substring(0, 50)}{proj.description.length > 50 ? '...' : ''}</div>}</td>
                  <td><Badge bg={getStatusColor(proj.status)} className="text-capitalize p-2">{proj.status || 'N/A'}</Badge></td>
                  <td className="text-end">{proj.budget !== null ? formatCurrency(proj.budget, proj.currency) : <span className="text-secondary">N/A</span>}</td>

                  {/* --- REVERTED Spent Cell --- */}
                  <td className="text-end">
                      {/* Use total_expenditure from backend */}
                      {proj.budget !== null ? formatCurrency(proj.total_expenditure ?? 0, proj.currency) : <span className="text-secondary">-</span>}
                  </td>
                  {/* --- END REVERT --- */}

                   {/* Remaining Budget Cell */}
                   <td className={`text-end fw-bold ${proj.budget !== null && (proj.remaining_budget ?? proj.budget) < 0 ? 'text-danger' : ''}`}>
                      {proj.budget !== null ? formatCurrency(proj.remaining_budget ?? proj.budget, proj.currency) : <span className="text-secondary">-</span>}
                  </td>
                  {/* Actions Cell */}
                  <td className="text-center">
                      <Button variant="outline-light" size="sm" onClick={() => handleShowEditModal(proj)} className="me-2 py-1 px-2" title="Edit Project Details"><i className="bi bi-pencil-fill me-1"></i> Edit</Button>
                      <Button variant="outline-danger" size="sm" onClick={() => handleDelete(proj.id)} title="Delete Project" className="py-1 px-2"><i className="bi bi-trash-fill me-1"></i> Delete</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      )}

      {/* --- Project Modal --- */}
      <Modal show={showModal} onHide={handleCloseModal} backdrop="static" keyboard={false} centered>
        <Modal.Header closeButton={!isSubmitting} closeVariant="white" className="bg-dark border-secondary">
          <Modal.Title className="text-light"> {isEditing ? 'Edit Project' : 'Create New Project'} </Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body className="bg-dark text-light">
             {modalError && <Alert variant="danger" className="bg-dark text-light border-danger" onClose={() => setModalError(null)} dismissible>{modalError}</Alert>}
             {/* Project Form Fields */}
             <Form.Group className="mb-3" controlId="projectName">{/* ... Name ... */}</Form.Group>
             <Form.Group className="mb-3" controlId="projectDescription">{/* ... Description ... */}</Form.Group>
             <Row className="mb-3"> <Col md={7}><Form.Group controlId="projectBudget">{/* ... Budget ... */}</Form.Group></Col> <Col md={5}><Form.Group controlId="projectCurrency">{/* ... Currency ... */}</Form.Group></Col> </Row>

             {/* --- Conditionally Render Status Row --- */}
             {isEditing && (
                 <Row className="mb-3">
                     <Col md={6}>
                         <Form.Group controlId="projectStatus">
                             <Form.Label>Status</Form.Label>
                             <Form.Select name="status" value={formData.status} onChange={handleInputChange} className="bg-secondary text-white border-secondary" disabled={isSubmitting}>
                                 {projectStatuses.map(status => (<option key={status} value={status}>{status}</option>))}
                             </Form.Select>
                         </Form.Group>
                     </Col>
                 </Row>
             )}
             {/* --- End Conditional Row --- */}

             {/* Dates */}
             <Row> <Col md={6}><Form.Group className="mb-3" controlId="projectStartDate">{/* ... Start Date ... */}</Form.Group></Col> <Col md={6}><Form.Group className="mb-3" controlId="projectEndDate">{/* ... End Date ... */}</Form.Group></Col> </Row>
          </Modal.Body>
          <Modal.Footer className="bg-dark border-secondary">
            <Button variant="secondary" onClick={handleCloseModal} disabled={isSubmitting}>Cancel</Button>
            <Button variant="primary" type="submit" disabled={isSubmitting}> {isSubmitting ? (<>...</>) : (isEditing ? 'Save Changes' : 'Create Project')} </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </>
  );
}

export default ProjectsTable;
