import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import { API_BASE_URL } from '../../App';
import { Table, Button, Spinner, Alert, Modal } from 'react-bootstrap';
import ExpenditureForm from './ExpenditureForm'; // Import the modal form
import { formatCurrency, formatDate } from '../../utils/formatting'; // Import formatters

function ExpendituresTable({ projectId, onExpenditureUpdate }) { // Receive projectId and callback
  const { token } = useAuth();
  const [expenditures, setExpenditures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [showAddModal, setShowAddModal] = useState(false);
  // Optional: State for editing/deleting expenditures can be added later
  // const [expenditureToEdit, setExpenditureToEdit] = useState(null);
  // const [showEditModal, setShowEditModal] = useState(false);

  const fetchExpenditures = useCallback(async () => {
    if (!projectId) return; // Don't fetch if no project is selected

    setLoading(true);
    setError(null);
    try {
      // Use the nested route to get expenditures for the specific project
      const response = await fetch(`${API_BASE_URL}/projects/${projectId}/expenditures`, {
        headers: {
          Authorization: `Bearer ${token}`,
          Accept: 'application/json',
        },
      });
      if (!response.ok) {
          throw new Error(`HTTP error ${response.status}: Could not fetch expenditures.`);
      }
      const data = await response.json();
      // Assuming backend returns paginated data or direct array
      setExpenditures(data.data ? data.data : (Array.isArray(data) ? data : []));
    } catch (e) {
      console.error("Fetch expenditures error:", e);
      setError(e.message || 'Failed to load expenditures.');
    } finally {
      setLoading(false);
    }
  }, [projectId, token]);

  useEffect(() => {
    fetchExpenditures();
  }, [fetchExpenditures]); // Re-run when fetchExpenditures changes (i.e., projectId changes)

  // --- Modal Handlers ---
  const handleShowAddModal = () => setShowAddModal(true);
  const handleCloseAddModal = () => setShowAddModal(false);

  const handleSaveSuccess = () => {
    handleCloseAddModal(); // Close the add modal
    fetchExpenditures();    // Refresh the expenditures list
    if (onExpenditureUpdate) {
         onExpenditureUpdate(); // Call the callback passed from ProjectsTable to refresh project list
    }
  };

  // --- Optional: Delete Handler ---
  const handleDeleteExpenditure = async (expenditureId) => {
      if (!window.confirm("Are you sure you want to delete this expenditure?")) return;
      setError(null);
      try {
          // Use the non-nested route for delete (if using shallow routes)
          const response = await fetch(`${API_BASE_URL}/expenditures/${expenditureId}`, {
              method: 'DELETE',
              headers: { Authorization: `Bearer ${token}`, Accept: 'application/json' },
          });
          if (!response.ok && response.status !== 204) {
              let msg = `HTTP error ${response.status}`;
              try { const err = await response.json(); msg = err.message || msg; } catch(e) {}
              throw new Error(msg);
          }
          fetchExpenditures(); // Refresh list
          if (onExpenditureUpdate) {
              onExpenditureUpdate(); // Refresh project list
          }
      } catch (e) {
          console.error("Delete expenditure error:", e);
          setError(`Failed to delete expenditure: ${e.message}`);
      }
  }
  // --- End Optional Delete ---


  // --- Render Logic ---
  if (loading) return <div className="text-center text-light"><Spinner animation="border" size="sm" /> Loading Expenditures...</div>;
  if (error) return <Alert variant="warning" className="bg-dark text-warning border-warning">{error}</Alert>;

  return (
    <>
      <div className="d-flex justify-content-end mb-3">
        <Button variant="success" onClick={handleShowAddModal}>
          <i className="bi bi-plus-lg me-1"></i> Add Expenditure
        </Button>
      </div>

      {expenditures.length === 0 && <Alert variant="secondary" className="bg-dark text-secondary border-secondary">No expenditures recorded for this project yet.</Alert>}

      {expenditures.length > 0 && (
        <Table variant="dark" striped hover responsive="sm" size="sm">
          <thead>
            <tr>
              <th>Description</th>
              <th className="text-end">Amount</th>
              <th>Date</th>
              <th>Recorded By</th>
              <th className="text-center">Actions</th>
            </tr>
          </thead>
          <tbody>
            {expenditures.map(exp => (
              <tr key={exp.id}>
                <td>{exp.description}</td>
                <td className="text-end">{formatCurrency(exp.amount, exp.project?.currency || 'USD')}</td> {/* Assuming project currency is available or default */}
                <td>{formatDate(exp.expense_date)}</td>
                <td>{exp.recorder?.name || 'N/A'}</td>
                <td className="text-center">
                   {/* Optional Edit Button
                   <Button variant="outline-light" size="xs" className="me-1 py-0 px-1" title="Edit Expenditure">
                       <i className="bi bi-pencil-fill small"></i>
                   </Button> */}
                   <Button
                       variant="outline-danger"
                       size="xs" // Extra small button
                       className="py-0 px-1" // Minimal padding
                       onClick={() => handleDeleteExpenditure(exp.id)}
                       title="Delete Expenditure"
                    >
                       <i className="bi bi-trash-fill small"></i>
                   </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      {/* Render the Expenditure Form Modal */}
      <ExpenditureForm
         show={showAddModal}
         handleClose={handleCloseAddModal}
         projectId={projectId} // Pass the current project ID
         // expenditureToEdit={expenditureToEdit} // Pass data if implementing edit
         onSaveSuccess={handleSaveSuccess} // Pass the success callback
      />
    </>
  );
}

export default ExpendituresTable;
