import React from 'react';
import { Link, Outlet, useNavigate, NavLink } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
// --- Import the NotificationBell component ---
import NotificationBell from '../Notifications/NotificationBell'; // Adjust path if needed

function Dashboard() {
  const { logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout(); // Call the logout function from context
    navigate('/login'); // Redirect to login page
  };

  return (
    // Main container using flex column to push footer down
    <div className="d-flex flex-column min-vh-100" style={{ background: 'linear-gradient(135deg, #1f1f1f, #121212)' }}>

      {/* --- Navbar Section --- */}
      <Navbar expand="lg" variant="dark" bg="dark" className="shadow-sm px-3"> {/* Added some padding */}
        <Container fluid>
          {/* Brand Logo/Name */}
          <Navbar.Brand as={Link} to="/dashboard" className="fw-bold text-white">
            <i className="bi bi-kanban-fill me-2"></i>Klick Inc. {/* Example Icon */}
          </Navbar.Brand>

          {/* Navbar Toggle Button (for smaller screens) */}
          <Navbar.Toggle aria-controls="dashboard-navbar-nav" />

          {/* Navbar Collapsible Content */}
          <Navbar.Collapse id="dashboard-navbar-nav">

            {/* Left-aligned Navigation Links */}
            <Nav className="me-auto mb-2 mb-lg-0"> {/* Adjusted margins for collapse */}
              <Nav.Link
                as={NavLink}
                to="/dashboard/projects"
                className={({ isActive }) => isActive ? 'text-white fw-bold' : 'text-secondary'}
              >
                Projects
              </Nav.Link>
              <Nav.Link
                as={NavLink}
                to="/dashboard/tasks"
                className={({ isActive }) => isActive ? 'text-white fw-bold' : 'text-secondary'}
              >
                Tasks
              </Nav.Link>
              {/* Add other main navigation links here */}
            </Nav>

            {/* Right-aligned Items */}
            <Nav className="ms-auto d-flex align-items-center"> {/* Use Nav for alignment */}
              {/* --- Notification Bell Component --- */}
              <NotificationBell />
              {/* --- End Notification Bell --- */}

              {/* Logout Button */}
              <Button variant="outline-light" onClick={handleLogout} className="ms-2"> {/* Margin Start for spacing */}
                <i className="bi bi-box-arrow-right me-1"></i>Logout
              </Button>
            </Nav>
            {/* --- End Right-aligned Items --- */}

          </Navbar.Collapse>
          {/* End Navbar Collapsible Content */}

        </Container>
      </Navbar>
      {/* --- End Navbar Section --- */}


      {/* --- Main Content Area --- */}
      <Container fluid as="main" className="flex-grow-1 my-4">
        {/* Inner container for content styling and centering (optional) */}
        <Container className="bg-dark text-white p-4 rounded shadow">
          {/* Child routes defined in App.js will render here */}
          <Outlet />
        </Container>
      </Container>
      {/* --- End Main Content Area --- */}


      {/* --- Footer --- */}
      <footer className="text-center text-secondary py-3 mt-auto bg-dark"> {/* Footer stays at bottom */}
        Â© {new Date().getFullYear()} Klick Inc. All rights reserved.
      </footer>
      {/* --- End Footer --- */}

    </div>
  );
}

export default Dashboard;
