import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import { API_BASE_URL } from '../../App';
import { Table, Button, Spinner, Alert } from 'react-bootstrap';
import ExpenditureForm from './ExpenditureForm';
import { formatCurrency, formatDate } from '../../utils/formatting';

function ExpendituresTable({ projectId, onExpenditureUpdate }) {
  const { token } = useAuth();
  const [expenditures, setExpenditures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [expenditureToEdit, setExpenditureToEdit] = useState(null);

  // Fetch Expenditures Logic (remains the same)
  const fetchExpenditures = useCallback(async () => { /* ... */ }, [projectId, token]);
  useEffect(() => { fetchExpenditures(); }, [fetchExpenditures]);

  // Modal Handlers (remain the same)
  const handleShowAddModal = () => { /* ... */ };
  const handleCloseAddModal = () => { /* ... */ };
  const handleShowEditModal = (expenditure) => { /* ... */ };
  const handleCloseEditModal = () => { /* ... */ };
  const handleSaveSuccess = () => { /* ... */ };

  // Delete Handler (remains the same)
  const handleDeleteExpenditure = async (expenditureId) => { /* ... */ };


  // --- Render Logic ---
  if (loading) return <div className="text-center text-light"><Spinner animation="border" size="sm" /> Loading Expenditures...</div>;
  if (error) return <Alert variant="warning" className="bg-dark text-warning border-warning">{error}</Alert>;

  return (
    <>
      <div className="d-flex justify-content-end mb-3">
        <Button variant="success" onClick={handleShowAddModal}>
          <i className="bi bi-plus-lg me-1"></i> Add Expenditure
        </Button>
      </div>

      {expenditures.length === 0 && <Alert variant="secondary" className="bg-dark text-secondary border-secondary">No expenditures recorded for this project yet.</Alert>}

      {expenditures.length > 0 && (
        <Table variant="dark" striped hover responsive="sm" size="sm">
          <thead>
            <tr>
              <th>Description</th>
              <th className="text-end">Amount</th>
              <th>Date</th>
              <th>Recorded By</th>
              <th className="text-center">Actions</th>
            </tr>
          </thead>
          <tbody>
            {expenditures.map(exp => (
              <tr key={exp.id}>
                <td>{exp.description}</td>
                {/* Use appropriate currency */}
                <td className="text-end">{formatCurrency(exp.amount, exp.project?.currency || 'USD')}</td>
                <td>{formatDate(exp.expense_date)}</td>
                <td>{exp.recorder?.name || 'N/A'}</td>
                <td className="text-center">
                   {/* --- UPDATED Edit Button --- */}
                   <Button
                       variant="outline-light"
                       size="sm" // Changed from xs to sm
                       className="me-2" // Added margin-end for spacing
                       onClick={() => handleShowEditModal(exp)}
                       title="Edit Expenditure"
                   >
                       <i className="bi bi-pencil-fill me-1"></i> Edit {/* Added Text & icon margin */}
                   </Button>
                   {/* --- END Edit Button --- */}

                   {/* --- UPDATED Delete Button --- */}
                   <Button
                       variant="outline-danger"
                       size="sm" // Changed from xs to sm
                       onClick={() => handleDeleteExpenditure(exp.id)}
                       title="Delete Expenditure"
                    >
                       <i className="bi bi-trash-fill me-1"></i> Delete {/* Added Text & icon margin */}
                   </Button>
                   {/* --- END Delete Button --- */}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      {/* Add Modal Rendering */}
      <ExpenditureForm
         show={showAddModal}
         handleClose={handleCloseAddModal}
         projectId={projectId}
         onSaveSuccess={handleSaveSuccess}
      />

      {/* Edit Modal Rendering */}
      {expenditureToEdit && (
          <ExpenditureForm
             show={showEditModal}
             handleClose={handleCloseEditModal}
             expenditureToEdit={expenditureToEdit} // Pass data to edit
             onSaveSuccess={handleSaveSuccess}
             // projectId might not be needed if PUT route is /expenditures/{id}
          />
      )}
    </>
  );
}

export default ExpendituresTable;
